mapper.delete:
delete
from FAVORITE
where USER_ID = #{userId}
  AND ITEM_ID = #{itemId}

mapper.select:
select CASE
               WHEN Count(FAVORITE_ID) > 0 THEN 1
               ELSE 0 END RESULT
from FAVORITE
where USER_ID = #{userId}
  AND ITEM_ID = #{itemId}

select count(*)
from FAVORITE
where ITEM_ID = #{itemId}
